const express = require("express");
const app = express();
const mongoose = require("mongoose");

const port = 3000;
const host = "localhost";

app.use(express.json());
app.use(express.urlencoded({ extended: true }));
app.use(express.static("public"));

app.get(`/`, (req, res) => {
  res.sendFile(__dirname, "index.html");
});

app.listen(port, () => {
  console.log(`server is running on port : ${port}`);
  console.log(`Visit : http://${host}:${port}`);
});

// mongoDb connection
mongoose
  .connect(`mongodb://127.0.0.1:27017/myapp`)
  .then(() => {
    console.log("Connected to MongoDB successfully");
  })
  .catch((e) => {
    console.log(`internal server error` + e);
  });

// db schemas
const userSchema = mongoose.Schema({
  name: {
    type: String,
    required: true,
  },
  email: {
    type: String,
    required: true,
  },
  age: {
    type: Number,
    required: true,
  },
  gender: {
    type: String,
    required: true,
  },
  password: {
    type: String,
    required: true,
  },
  marks: {
    type: Number,
    required: true,
  },
});

// db models
const userModel = mongoose.model("user", userSchema);

// oprations

// login
app.post(`/api/login/`, async (req, res) => {
  const { name, password } = req.body;
  try {
    const user = await userModel.findOne({ name, password });
    if (user) {
      res
        .status(200)
        .json({ success: true, message: "Login successful", user });
    } else {
      res.status(401).json({ success: false, message: "Invalid credentials" });
    }
  } catch (error) {
    res.status(500).json({ success: false, message: "Server error", error });
  }
});

// add user
app.post(`/api/add/`, async (req, res) => {
  const newUser = new userModel(req.body);
  await newUser.save();
  res
    .status(201)
    .json({ success: true, message: "User added successfully", user: newUser });
});

// get users
app.get("/api/getAll/", async (req, res) => {
  const users = await userModel.find();
  if (users) {
    res.json({ success: true, message: "all user fetched", user: users });
  } else {
    res.json({ success: true, message: "Users not fetched" });
  }
});

app.delete("/api/delete/:id", async (req, res) => {
  const id = req.params.id;
  try {
    const delUser = await userModel.findByIdAndDelete(id);
    if (delUser) {
      res.json({ success: true, message: "User deleted", delUser });
    } else {
      res.json({ success: false, message: "User not found" });
    }
  } catch (err) {
    res
      .status(500)
      .json({ success: false, message: "Error deleting user", error: err });
  }
});

app.put("/api/update/:id", async (req, res) => {
  const id = req.params.id;
  try {
    const updateUser = await userModel.findByIdAndUpdate(id, req.body);
    if (updateUser) {
      res.json({ success: true, message: "update successfully", updateUser });
    }
  } catch (e) {
    res
      .status(500)
      .json({ success: false, message: "Error updating user", error: e });
  }
});
